INSERT INTO `wp_options` VALUES ('208', '_transient_dash_88ae138922fe95674369b1cb3d215a2b', '<div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'https://wordpress.org/news/2017/01/wordpress-4-7-2-security-release/\'>WordPress 4.7.2 Security Release</a> <span class=\"rss-date\">January 26, 2017</span><div class=\"rssSummary\">WordPress 4.7.2 is now available. This is a security release for all previous versions and we strongly encourage you to update your sites immediately. WordPress versions 4.7.1 and earlier are affected by three security issues: The user interface for assigning taxonomy terms in Press This is shown to users who do not have permissions to use it. [&hellip;]</div></li></ul></div><div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'https://wptavern.com/solving-the-mystery-of-how-people-actually-use-wordpress\'>WPTavern: Solving the Mystery of How People Actually Use WordPress</a></li><li><a class=\'rsswidget\' href=\'https://wptavern.com/buddypress-2-8-boosts-minimum-php-requirement-adds-twenty-seventeen-companion-stylesheet\'>WPTavern: BuddyPress 2.8 Boosts Minimum PHP Requirement, Adds Twenty Seventeen Companion Stylesheet</a></li><li><a class=\'rsswidget\' href=\'https://wptavern.com/composing-a-wordpress-development-environment-with-docker\'>WPTavern: Composing a WordPress Development Environment with Docker</a></li></ul></div><div class=\"rss-widget\"><ul><li class=\"dashboard-news-plugin\"><span>Popular Plugin:</span> SiteOrigin Widgets Bundle&nbsp;<a href=\"plugin-install.php?tab=plugin-information&amp;plugin=so-widgets-bundle&amp;_wpnonce=31f0c8950d&amp;TB_iframe=true&amp;width=600&amp;height=800\" class=\"thickbox open-plugin-details-modal\" aria-label=\"Install SiteOrigin Widgets Bundle\">(Install)</a></li></ul></div>', 'no');

