INSERT INTO `wp_posts` VALUES ('20', '1', '2017-02-20 09:50:54', '2017-02-20 09:50:54', 'Most custom post types in WordPress will need (or could benefit from) a unique set metaboxes for entering information.\r\n\r\nFor example, a “photography” post type might need fields for “location of photo”, “type of camera”, etc. And an “event” post type would probably need a “location” and an “event date”.', 'custom post types', '', 'publish', 'open', 'closed', '', 'custom-post-types', '', '', '2017-02-21 06:44:08', '2017-02-21 06:44:08', '', '0', 'http://localhost:8888/?post_type=portfolio&#038;p=20', '0', 'portfolio', '', '0');
